SELECT (SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=nt.codAlmacen) as almacendestino, tr.descripcion ,  
       td.sigla ,  IFNULL(nt.serie,"") as serie,nt.numdocumento, nt.fechaingreso, nt.comentario, 
       (SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=nt.codalmacenemisor) as almacenorigen,
				e.razonsocial AS razonsocialempre,e.ruc AS rucempre,e.direccion AS direccionempre,e.telefono AS telefonoempre,
				p.referencia, p.descripcion as producto,u.descripcion as unidad,dt.cantidad as cantidad
FROM 		notaingreso nt
INNER JOIN transaccion tr ON nt.codTransaccion = tr.codTransaccion
LEFT OUTER JOIN tipodocumento td on nt.codTipoDocumento = td.codTipoDocumento
LEFT OUTER JOIN proveedor pr on nt.codProveedor = pr.codProveedor
LEFT OUTER JOIN orden_compra orde on orde.codOrdenCompra=nt.CodOrdenCompra
LEFT OUTER JOIN serie s on s.codSerie=orde.codserie
INNER JOIN almacen al ON al.codAlmacen=nt.codAlmacen
INNER JOIN empresa e ON e.codEmpresa = al.codEmpresa
INNER JOIN detallenotaingreso dt ON dt.codNotaIngreso=nt.codNotaIngreso
INNER JOIN producto p ON dt.codProducto = p.codProducto
INNER JOIN unidadmedida u ON dt.unidadingresada = u.codUnidadMedida
WHERE 	nt.codNotaIngreso = codnota;
END
;;
DELIMITER ;

