-- ----------------------------
-- Procedure structure for UtilidadProductos
-- ----------------------------
DROP PROCEDURE IF EXISTS `UtilidadProductos`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `UtilidadProductos`(fecha1 date, fecha2 date,codalma int(11))
BEGIN
	#Routine body goes here...
	SELECT pr.codProducto,
					pr.referencia,
					pr.descripcion,
					(dfv.cantidad) AS Cantidad,
					(dfv.valoreal) as ValVentaUnidad,
					(dfv.valorpromediosoles) as ValPromUniadad,
					(dfv.valoreal * SUM(dfv.cantidad)) as ValorVenta,
					(dfv.valorpromediosoles * SUM(dfv.cantidad)) as ValPromedio,
					(((dfv.valoreal * dfv.cantidad) - (dfv.valorpromediosoles * dfv.cantidad)) * dfv.cantidad) as UtilBruta,
					emp.ruc,suc.descripcion, suc.ubicacion, suc.nombre, suc.telefono,fecha1,fecha2,
					CONCAT(td.sigla,"-",fv.serie,"-",fv.numDocumento) AS num_doc,fv.fecharegistro,
					IFNULL(v.nombre,"Sin vendedor")as NomVendedor
		FROM factura_venta fv 
		INNER JOIN detallefactura_venta dfv ON fv.codFacturaV=dfv.codFacturaV
		INNER JOIN sucursal suc ON suc.codSucursal = fv.codsucursal
		INNER JOIN empresa emp ON emp.codEmpresa=suc.codEmpresa
		INNER JOIN producto pr ON pr.codProducto=dfv.codProducto
		LEFT JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento
		LEFT JOIN vendedor v ON v.codVendedor=fv.codVendedor
		WHERE fv.estado=1 AND fv.anulado=0 AND fv.codTransaccion=7  -- AND (fv.fechasalida=DATE("2017-09-26"))
		AND fv.codAlmacen=codalma AND fv.fechasalida BETWEEN DATE(fecha1) AND DATE(fecha2) 
		GROUP BY num_doc	;
 

END
;;
DELIMITER ;

