-- ----------------------------
-- Procedure structure for ValidarProductoBonificado
-- ----------------------------
DROP PROCEDURE IF EXISTS `ValidarProductoBonificado`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ValidarProductoBonificado`(codalma int(11), usu int(11))
BEGIN
	
  DECLARE codpro INT(11);

  SELECT IFNULL(dt.codProducto,0) INTO codpro 
  FROM detallenotaingreso dt INNER JOIN notaingreso nt ON dt.codNotaIngreso=nt.codNotaIngreso
  WHERE dt.codProducto NOT IN (SELECT d.codProducto FROM detalleorden_compra d WHERE d.codOrdenCompra=nt.CodOrdenCompra) 
        AND nt.codTransaccion = 14 AND nt.codAlmacen=codalma;

  IF codpro = 0 THEN
   
			SELECT  dt.codNotaIngreso AS codNota, dt.codDetalleIngreso as codDetalle, dt.codProducto, p.referencia, p.descripcion as producto,dt.moneda,
							u.codUnidadMedida, u.descripcion as unidad,dt.serielote, sum(dt.cantidadpendiente) as cantidad ,
							0.0000, 0.0000, dt.descuento1,dt.descuento2,dt.descuento3,dt.montodscto as montodscto,
							(dt.importe-dt.igv)  as valorventa, dt.igv as igv, dt.importe as importe , dt.precioreal as precioreal, dt.valoreal as valoreal, dt.flete 
			FROM 		detallenotaingreso dt LEFT JOIN producto p ON dt.codProducto = p.codProducto
						INNER JOIN unidadmedida u ON dt.unidadingresada = u.codUnidadMedida 
						INNER JOIN notaingreso nt ON dt.codNotaIngreso=nt.codNotaIngreso 
						INNER JOIN detallenotaorden dc on  dc.codorden=nt.CodOrdenCompra
						INNER JOIN proveedor prov ON nt.codProveedor=prov.codProveedor
			WHERE dt.estado = 1  and nt.codNotaIngreso=dc.codnota
						AND dt.codAlmacen=codalma  and dc.codalmacen=codalma and dc.codusuario=usu
			GROUP BY p.codProducto
			ORDER BY dt.codDetalleIngreso;

  END IF;

END
;;
DELIMITER ;

