-- ----------------------------
-- Procedure structure for VerificaSaldoCaja
-- ----------------------------
DROP PROCEDURE IF EXISTS `VerificaSaldoCaja`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `VerificaSaldoCaja`(codSucur int(11))
BEGIN

DECLARE dep BIT(1);
DECLARE cant INT;
DECLARE fec DATETIME;

SELECT COUNT(*) INTO cant FROM flujocaja WHERE codSucursal = codSucur and estado = 1;
SELECT IFNULL(fechaapertura,NOW()) INTO fec FROM flujocaja WHERE codSucursal = codSucur and estado = 1 and DATE(fecharegistro) = DATE(NOW());

IF cant > 1 THEN
    SELECT deposito INTO dep FROM flujocaja WHERE codSucursal = codSucur AND estado = 1 ORDER BY fecharegistro desc LIMIT 1,1;
ELSE
    -- SELECT IFNULL((SELECT deposito FROM flujocaja WHERE codSucursal = 1 AND estado = 1 ORDER BY fecharegistro desc LIMIT 0,1),0) INTO dep;
    SELECT deposito INTO dep FROM flujocaja WHERE codSucursal = codSucur AND estado = 1 ORDER BY fecharegistro desc LIMIT 0,1;
END IF;

IF dep = 1 THEN 
  IF cant > 1  THEN
			SELECT IFNULL(montoApertura,0.000)AS montoApertura, IFNULL(montoIngresado,0.000)AS montoIngresado, 
						 IFNULL(montoDepositado,0.000)AS montoDepositado, IFNULL(montoDisponible,0.000)AS montoDisponible
			FROM flujocaja 
			WHERE codSucursal = codSucur AND estado = 1
			ORDER BY fecharegistro DESC LIMIT 1,1;
  ELSE 
      SELECT IFNULL(montoApertura,0.000)AS montoApertura, IFNULL(montoIngresado,0.000)AS montoIngresado, 
						 IFNULL(montoDepositado,0.000)AS montoDepositado, IFNULL(montoDisponible,0.000)AS montoDisponible
			FROM flujocaja 
			WHERE codSucursal = codSucur AND estado = 1
			ORDER BY fecharegistro DESC LIMIT 0,1;
  END IF;
ELSEIF dep = 0 THEN 
  SELECT IFNULL(montoApertura,0.000)AS montoApertura, IFNULL(SUM(montoIngresado),0.000)AS montoIngresado, 
						 IFNULL(SUM(montoDepositado),0.000)AS montoDepositado, IFNULL(SUM(montoDisponible),0.000)AS montoDisponible 
			FROM flujocaja 
			WHERE codSucursal = codSucur AND estado = 1 and deposito = 0 and DATE(fecharegistro) <= DATE(NOW())
						-- and DATE(fecharegistro) <> DATE(NOW())
			GROUP BY codSucursal;
ELSEIF ISNULL(dep) THEN 
  SELECT IFNULL(montoApertura,0.000)AS montoApertura, IFNULL(montoIngresado,0.000)AS montoIngresado, 
         IFNULL(montoDepositado,0.000)AS montoDepositado, IFNULL(montoDisponible,0.000)AS montoDisponible
  FROM flujocaja 
  WHERE codSucursal = codSucur AND estado = 1;
END IF;

END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for VerificaSaldoCajaChica
-- ----------------------------
DROP PROCEDURE IF EXISTS `VerificaSaldoCajaChica`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `VerificaSaldoCajaChica`(codSucur int(11), tip int(1))
BEGIN
SELECT 
IFNULL(ap.montoApertura,0),
IFNULL((SELECT SUM(r1.monto) FROM recibos r1 WHERE r1.tipocajachica = tip AND ingresoegreso = 1 AND DATE_FORMAT(r1.fecharegistro,'%Y-%m-%d %H:%m:%S') > DATE_FORMAT(ap.fecharegistro,'%Y-%m-%d %H:%m:%S')),0) AS montoIngreso,  
IFNULL((SELECT SUM(r1.monto) FROM recibos r1 WHERE r1.tipocajachica = tip AND ingresoegreso = 0 AND DATE_FORMAT(r1.fecharegistro,'%Y-%m-%d %H:%m:%S') > DATE_FORMAT(ap.fecharegistro,'%Y-%m-%d %H:%m:%S')),0) AS montoEgreso, 
IFNULL(((IFNULL(ap.montoApertura,0) + 
IFNULL((SELECT SUM(r1.monto) FROM recibos r1 WHERE r1.tipocajachica = tip AND ingresoegreso = 1 AND DATE_FORMAT(r1.fecharegistro,'%Y-%m-%d %H:%m:%S') > DATE_FORMAT(ap.fecharegistro,'%Y-%m-%d %H:%m:%S')),0)) -
IFNULL((SELECT SUM(r1.monto) FROM recibos r1 WHERE r1.tipocajachica = tip AND ingresoegreso = 0 AND DATE_FORMAT(r1.fecharegistro,'%Y-%m-%d %H:%m:%S') > DATE_FORMAT(ap.fecharegistro,'%Y-%m-%d %H:%m:%S')),0)),0) AS montoDisponible,

IFNULL(SUM(re.montopendiente),0), 
IFNULL(SUM(re.montorendido),0),
IFNULL(ap.codAperturaCajaChica,0) AS codAperturaCajaChica
FROM aperturacajachica ap
LEFT JOIN recibos re ON re.codSucursal=ap.codSucursal
WHERE ap.codSucursal = codSucur AND ap.estado = 1 AND  ap.tipo=tip
;
END
;;
DELIMITER ;

