DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `VerificaSaldoCajaChicaDiaria`(codSucur int(11), fecha1 date)
BEGIN
	#Routine body goes here...
/*SELECT IFNULL(montoApertura,0), IFNULL(totalIngreso,0), IFNULL(totalEgreso,0), IFNULL(totalDisponible,0)
FROM cajadiaria 
WHERE codSucursal = codSucur AND DATE(fechaApertura)=DATE(fecha1) AND estado = 1;*/

SELECT IFNULL(cd.montoApertura,0), 
IFNULL(SUM(ct.ingreso),0) as Tingreso, #ingreso
IFNULL(SUM(ct.egreso),0) as Tegreso, 
((IFNULL(cd.montoApertura,0) + IFNULL(SUM(ct.ingreso),0)) - IFNULL(SUM(ct.egreso),0)) AS saldo
