CREATE DEFINER=`root`@`localhost` FUNCTION `obtienepreciosoles_lista`(decimales int(11), precio decimal(10,4)) RETURNS decimal(10,4)
BEGIN

 DECLARE por INTEGER DEFAULT 1;
 DECLARE redon DECIMAL(10,4) DEFAULT 0;
 DECLARE dif DECIMAL(10,4) DEFAULT 0;

IF decimales=0 THEN 
SET por=1;
ELSEIF decimales=1 THEN
SET por=10;
ELSEIF decimales=2 THEN
SET por=100;
ELSEIF decimales=3 THEN
SET por=1000;
ELSE SET por=10000;
END IF;


set redon= ROUND(precio,decimales);

SET dif=precio-redon;

IF dif >0.0000 THEN 
	RETURN redon+(1/por);
ELSE
	RETURN redon;
END IF;


END
;;
DELIMITER ;

