CREATE TRIGGER `ActualizaComisionInsert` AFTER INSERT ON `detallenotaingreso` FOR EACH ROW BEGIN

DECLARE varComision float;
DECLARE varNota int;
DECLARE cod INTEGER DEFAULT 0;

SELECT codTransaccion INTO cod  FROM  notaingreso WHERE codNotaIngreso=NEW.codNotaIngreso;

SET varComision = (SELECT comision from producto WHERE codProducto=NEW.codProducto);
SET varComision = (varComision * (NEW.importe - NEW.igv)) /  100;
SET varNota = (SELECT codReferencia FROM notaingreso WHERE codNotaIngreso = NEW.codNotaIngreso);

IF cod<>15 or cod<>16 or cod<> 8 THEN

          IF varNota != 0 THEN
           
          UPDATE detallenotasalida SET comision = comision - varComision WHERE codNotaSalida = varNota AND codProducto = NEW.codProducto;          
          
          END IF;

END IF;

END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `ActualizaStockdisponible`;
DELIMITER ;;
CREATE TRIGGER `ActualizaStockdisponible` AFTER INSERT ON `detallenotaingreso` FOR EACH ROW BEGIN

DECLARE tc DECIMAL (10,3);
DECLARE fa DECIMAL (10,2);
DECLARE uq int;
DECLARE ult DECIMAL (10,4);
DECLARE cod INTEGER DEFAULT 0;
DECLARE codAlmacenEmisor INTEGER DEFAULT 0;
DECLARE codAlmacenReceptor INTEGER DEFAULT 0;

SELECT codTransaccion INTO cod  FROM  notaingreso WHERE codNotaIngreso=NEW.codNotaIngreso;
SELECT tr.codAlmacenOrigen, tr.codAlmacenDestino INTO codAlmacenEmisor, codAlmacenReceptor FROM notaingreso ni INNER JOIN transferencia tr on tr.codTransDir = ni.codReferencia WHERE ni.codTransaccion = cod AND ni.codNotaIngreso = NEW.codNotaIngreso;

SET ult = (SELECT ultprecio FROM productoalmacen WHERE codProducto = NEW.codProducto AND codAlmacen = codAlmacenEmisor);
SET tc = (SELECT venta FROM tipocambio WHERE date(fecha) = date(NEW.fechaingreso));
SET uq = (SELECT codUnidadMedida FROM producto WHERE codProducto = NEW.codProducto);
SET fa = (SELECT factor FROM unidadequivalente where codProducto = NEW.codProducto and compra_venta = 2 and codUnidadMedida = NEW.unidadingresada and codUndEqui = uq);

IF cod = 15  THEN

      IF EXISTS(SELECT * FROM productoalmacen pp WHERE pp.codProducto = NEW.codProducto and pp.codAlmacen=New.codAlmacen) THEN
                      
                UPDATE productoalmacen
	SET stockdisponible = stockdisponible + (NEW.cantidad * fa),  
	stockactual = stockactual + (NEW.cantidad * fa ),  
	soles = soles + ((NEW.valoreal * NEW.cantidad) ),
	totalingresos = totalingresos + (NEW.cantidad * fa),
	totalsolesingresos = totalsolesingresos + ((NEW.valoreal * NEW.cantidad) ),
	ultprecio = (NEW.valoreal)  / fa,
	Unidad = uq   
	WHERE codProducto = NEW.codProducto  and codAlmacen=NEW.codAlmacen ;
    ELSE               
               
                 INSERT INTO productoalmacen
                 (codProducto, codAlmacen, stockactual, stockdisponible, valorpromedio, valorpromediosoles, preciopromedio, preciovariable, 
                                                                                                                                                 soles, totalingresos, totalsolesingresos, totalsalidas, totalsolessalidas, ultprecio, codUser, fecharegistro, Unidad)
                 VALUES
                 (NEW.codProducto, New.codAlmacen,  (NEW.cantidad * fa),  (NEW.cantidad * fa), ((NEW.valoreal/tc) / fa), (NEW.valoreal / fa), ((NEW.precioreal)), 1, 
								((NEW.valoreal ) * NEW.cantidad), (NEW.cantidad * fa), ((NEW.valoreal) * NEW.cantidad), 0, 0, ((NEW.valoreal) / fa), NEW.codUser, NOW(), uq);
             
                    
     END IF;
END IF;

END
;;
