                        pendiente = pendiente - NEW.montocobrado
                 WHERE codFacturaV = NEW.codNota;
              END IF;
          END IF;
   ELSE
         UPDATE factura_venta
          SET abonado = total,
                 pendiente = 0,
                 cancelado = 1
          WHERE codFacturaV = NEW.codNota;
   END IF;
END IF;

END
