DROP TRIGGER IF EXISTS `ActualizaNotaInsertPagoAprobado`;
DELIMITER ;;
CREATE TRIGGER `ActualizaNotaInsertPagoAprobado` AFTER UPDATE ON `pago` FOR EACH ROW BEGIN

DECLARE tc_venta DECIMAL(10,3);
DECLARE tc_compra DECIMAL(10,3);
DECLARE sal DECIMAL(10,3);
DECLARE codsucur INTEGER (1);

SELECT tc.compra INTO tc_compra FROM tipocambio tc WHERE tc.fecha=date(NEW.fechapago);
SELECT tc.venta INTO tc_venta FROM tipocambio tc WHERE tc.fecha=date(NEW.fechapago);
SELECT codSucursal INTO codsucur FROM almacen WHERE codAlmacen=NEW.codAlmacen;
SELECT IFNULL(SUM(ccm.ingreso),0) - IFNULL(SUM(ccm.egreso),0) INTO sal FROM ctactemovimientos ccm WHERE ccm.codCuentaCorriente = NEW.codctacte and ccm.estado = 1;



                      IF NEW.codTipoPago=6 OR  NEW.codTipoPago=7 OR  NEW.codTipoPago=9 THEN 
                                                                                                     
                                         IF NEW.ingresoegreso=1 AND NEW.Aprobado=4  THEN
                                                                                                  
                                                  INSERT INTO ctactemovimientos
                                                               (codCuentaCorriente, codDocumento, codAlmacen, documento, numTransaccion, descripcion, moneda, tcventa, tccompra, ingreso,
                                                               saldo,codUser, fechaMovimiento, fechaRegistro, codPago, codBanco, codSucursal, tipo, ingresoegreso)
                                                  VALUES (NEW.codctacte,(SELECT f.codTipoDocumento FROM factura_venta f WHERE f.codFacturaV =NEW.codNota), NEW.codAlmacen,
                                                                (SELECT CONCAT(f.serie,'-',f.numDocumento) FROM factura_venta f WHERE f.codFacturaV = NEW.codNota),NEW.noperacion, 
                                                                (SELECT mp.descripcion FROM metodopago mp WHERE mp.codMetodoPago=NEW.codTipoPago), 
                                                                NEW.codMoneda, tc_venta, tc_compra, NEW.montocobrado, (sal+NEW.montocobrado), NEW.codUser, NEW.fechapago, NOW(), 
                                                                NEW.codPago, NEW.codBanco, codsucur, 2, NEW.ingresoegreso);
                                                                                 
                                                                IF NEW.codTipoPago = 6  THEN              /* DEPOSITO   */                                                                
                                                                           UPDATE caja
                                                                           SET totalDeposito = totalDeposito +  NEW.montocobrado
                                                                           WHERE codcaja =  NEW.codCaja AND codSucursal = codsucur;
                                                                END IF;  
                                                                    
                                                                IF NEW.codTipoPago =7  THEN              /* POR CHEQUE   */                                                                
                                                                           UPDATE caja
                                                                           SET totalCheque = totalCheque +  NEW.montocobrado
                                                                           WHERE codcaja =  NEW.codCaja AND codSucursal = codsucur;
                                                                END IF;                               

                                                                IF NEW.codTipoPago =9  THEN              /* TRANSFERENCIA   */                                                                
                                                                           UPDATE caja
                                                                           SET totalTransferencia = totalTransferencia +  NEW.montocobrado
                                                                           WHERE codcaja =  NEW.codCaja AND codSucursal = codsucur;
                                                                END IF;   
                                         END IF;
                      END IF;

END
;;
