DROP TRIGGER IF EXISTS `ActualizaPago_al_AnularVenta`;
DELIMITER ;;
CREATE TRIGGER `ActualizaPago_al_AnularVenta` AFTER UPDATE ON `pago` FOR EACH ROW BEGIN

DECLARE codigocaja INT(11);
DECLARE codigosucursal INT(11);
DECLARE estadocaja INT(11);
DECLARE TotMontoCobrado DECIMAL(10,5);


SELECT cj.codcaja INTO codigocaja FROM cajamovimiento cj WHERE cj.codPago = NEW.codPago;
SELECT cj.codSucursal INTO codigosucursal FROM cajamovimiento cj WHERE cj.codPago =  NEW.codPago;
SELECT estado  INTO estadocaja FROM caja WHERE codcaja = codigocaja;
SELECT (p.montocobrado - c.monto) INTO TotMontoCobrado FROM pago p INNER JOIN cajamovimiento c  ON p.codPago=c.codPago WHERE p.codPago= NEW.codPago;

                 IF NEW.estado <> OLD.estado THEN
                       
                              IF NEW.estado = 0 THEN  

                                                               UPDATE cajamovimiento
                                                               SET estado = 0
                                                               WHERE codPago = NEW.codPago;

                                                                IF NEW.codTipoPago = 6  THEN              /* DEPOSITO   */                                                                
                                                                           UPDATE caja
                                                                           SET totalDeposito =TotMontoCobrado /*-   NEW.montocobrado */
                                                                           WHERE codcaja =  codigocaja AND codSucursal = codigosucursal AND codalmacen = NEW.codAlmacen;
                                                                END IF;  
                                                                    
                                                                IF NEW.codTipoPago =7  THEN              /* POR CHEQUE   */                                                                
                                                                           UPDATE caja
                                                                           SET totalCheque = TotMontoCobrado  /*totalCheque -  NEW.montocobrado*/
                                                                           WHERE codcaja =  codigocaja AND codSucursal = codigosucursal AND codalmacen = NEW.codAlmacen;
                                                                END IF;                               

                                                                IF NEW.codTipoPago =9  THEN              /* TRANSFERENCIA   */                                                                
                                                                           UPDATE caja
                                                                           SET totalTransferencia = TotMontoCobrado  /*totalTransferencia -  NEW.montocobrado*/
                                                                           WHERE codcaja =  codigocaja AND codSucursal = codigosucursal AND codalmacen = NEW.codAlmacen;
                                                                END IF;          

                                                                IF NEW.codTipoPago =5  THEN              /* EFECTIVO   */  

                                                                            IF estadocaja = 1  THEN 
                                                                                
                                                                                               UPDATE caja 
                                                                                               SET totalIngreso = (totalIngreso - NEW.montocobrado),
                                                                                                      totalVentaEfectivo = (totalVentaEfectivo - NEW.montocobrado), 
                                                                                                      totalDisponible = (totalDisponible  - NEW.montocobrado)
                                                                                               WHERE codcaja =  codigocaja AND codSucursal = codigosucursal AND codalmacen = NEW.codAlmacen;
                                                                                    
                                                                             END IF;
                                                                                
                                                                              IF estadocaja = 0  THEN 
                                                                                
                                                                                               UPDATE caja 
                                                                                               SET totalIngreso = (totalIngreso - NEW.montocobrado),
                                                                                                      totalVentaEfectivo = (totalVentaEfectivo - NEW.montocobrado), 
                                                                                                      totalDisponible = (totalDisponible  - NEW.montocobrado),
                                                                                                      montocierre = (montocierre - NEW.montocobrado)
                                                                                               WHERE codcaja =  codigocaja AND codSucursal = codigosucursal AND codalmacen = NEW.codAlmacen;
                                                                                    
                                                                             END IF; 
                                                                END IF;       
                        
                              END IF;
                              
                 END IF;       
END
;;
